import React from "react";

export default function QuestionCard({ question, selectedIndex, onSelect }) {
  return (
    <div>
      <div className="question-meta">{question.category} â€¢ {question.difficulty}</div>
      <div className="question-title">{question.question}</div>

      <fieldset className="options" aria-label="Answer options">
        {question.options.map((opt, idx) => {
          const selected = selectedIndex === idx;
          return (
            <label
              key={idx}
              className={`option ${selected ? "selected" : ""}`}
              onClick={() => onSelect(idx)}
              tabIndex={0}
              onKeyDown={(e) => {
                if (e.key === "Enter" || e.key === " ") onSelect(idx);
              }}
              aria-pressed={selected}
            >
              <span className="dot" aria-hidden="true" />
              <span>{opt}</span>
            </label>
          );
        })}
      </fieldset>
    </div>
  );
}
